final ChemistryTopic1 =
    '[{"id": "1","question":"Kim loại nào sau đây tác dụng với nước ở nhiệt độ thường?","trueAnswer": " Na .","falseAnswer1": " Be.","falseAnwser2": " Cu.","falseAnwer3": " Ag."},{"id": "2   ","question":"Trong phản ứng của kim loại Al với khí Cl2, một nguyên tử Al nhường bao nhiêu electron?","trueAnswer": " 4.","falseAnswer1": " 3.","falseAnwser2": " 2.","falseAnwer3": " 1."},{"id": "3","question":"Chất nào sau đây là amin bậc hai?","trueAnswer": " (CH3)3N.","falseAnswer1": " C6H5NH2.","falseAnwser2": " C2H5NH2.","falseAnwer3": " CH3NHCH3."},{"id": "4","question":"Kim loại nào sau đây có độ cứng lớn nhất?","trueAnswer": " Cr.","falseAnswer1": " W.","falseAnwser2": " Fe.","falseAnwer3": " Al."},{"id": "5","question":"Chất nào sau đây khi thủy phân hoàn toàn trong môi trường axit, thu được sản phẩm hữu cơ chỉ chứa glucozơ?","trueAnswer": " Fructozơ.","falseAnswer1": " Saccarozơ.","falseAnwser2": " Protein.","falseAnwer3": " Tinh bột."},{"id": "6","question":"Trong công nghiệp, kim loại nào sau đây được điều chế bằng phương pháp nhiệt luyện?","trueAnswer": " Fe.","falseAnswer1": " Mg.","falseAnwser2": " Al.","falseAnwer3": " K."},{"id": "7","question":"Tác nhân chủ yếu gây nên hiện tượng “mưa axit” là","trueAnswer": " SO2 và NO2.","falseAnswer1": " hơi nước và CH4.","falseAnwser2": " hơi nước và CH4.","falseAnwer3": " CO và CH4."},{"id": "8","question":"Oxit nào sau đây tác dụng với H2O tạo thành dung dịch kiềm?","trueAnswer": " CO2.","falseAnswer1": " CrO3.","falseAnwser2": " Cr2O3.","falseAnwer3": " Na2O."},{"id": "9","question":"Crom(VI) oxit là chất rắn, màu đỏ thẫm, là một oxit axit có tính oxi hóa mạnh. Công thức của crom(VI) oxit là","trueAnswer": " CrO3.","falseAnswer1": " CrO.","falseAnwser2": " Cr2O3.","falseAnwer3": " Cr2O5."},{"id": "10","question":"Polietilen được tạo thành bằng phản ứng trùng hợp chất nào sau đây?","trueAnswer": " Stiren.","falseAnswer1": " Propilen.","falseAnwser2": " Etilen.","falseAnwer3": " Buta-1,3-dien."},{"id": "11","question":"Cô