    '[  {"id": "1","question":"Phương pháp nhiệt luyện có thể áp dụng để điều chế cặp kim loại nào sau đây?","trueAnswer": " Cu, Fe.","falseAnswer1": " Mg, Fe.","falseAnwser2": " Fe, Al.","falseAnwer3": " Al, Zn."},{"id": "2","question":"Kim loại nào sau đây tác dụng với nước ở điều kiện thường?","trueAnswer": " Cu.","falseAnswer1": " Cr.","falseAnwser2": " Ag.","falseAnwer3": " Ba ."},{"id": "3","question":"Chất nào sau đây có chứa một liên kết đôi C=C trong công thức cấu tạo?","trueAnswer": " C2H4.","falseAnswer1": " CH4.","falseAnwser2": " C2H2.","falseAnwer3": " CH3COOH."},{"id": "4   ","question":"Trong công nghiệp, để chuyển hóa chất béo lỏng thành chất béo rắn người ta cho chất béo lỏng phản ứng với?","trueAnswer": " H2O (xúc tác Ni, t°).","falseAnswer1": " Dung dịch KOH.","falseAnwser2": " H2O (xúc tác axit).","falseAnwer3": " Dung dịch NaOH."},{"id": "5","question":"Công thức tổng quát của amin no, đơn chức, mạch hở là?","trueAnswer": " CnH2n+2N (n ≥ 1)","falseAnswer1": " CnH2n+3N (n ≥ 1).","falseAnwser2": " CnH2n+1NO2 (n ≥ 2).","falseAnwer3": " CnH2n+2N2 (n ≥ 1)."},{"id": "6","question":"Metyl axetat có công thức cấu tạo thu gọn là?","trueAnswer": " CH3COOCH3.","falseAnswer1": " CH3COOC2H5.","falseAnwser2": " C2H3COOCH3.","falseAnwer3": " HCOOC2H5."},{"id": "7   ","question":"Trong phòng thí nghiệm, để bảo quản kim loại Na người ta ngâm chúng trong?","trueAnswer": " dầu hỏa .","falseAnswer1": " dung dịch axit axetic .","falseAnwser2": " ancol etylic .","falseAnwer3": " nước ."},{"id": "8","question":"Axit amino axetic không phản ứng được với dung dịch chất nào sau đây?","trueAnswer": " NaCl.","falseAnswer1": " NaOH.","falseAnwser2": " KOH.","falseAnwer3": " HCl."},{"id": "9   ","question":"Kim loại cứng nhất, được dùng để chế tạo thép không gỉ là?","trueAnswer": " Fe.","falseAnswer1": " Os.","falseAnwser2": " Cr","falseAnwer3": " W."},{"id": "10   ","question":"Bột khai (thành phần hóa học chính là amoni hidrocacbonat) được sử dụng để tạo độ tơi xốp cho mộ