    '[{"id": "1","question":"Khí nào sau đây là tác nhân chủ yếu gây mưa axit?","trueAnswer": " SO2.","falseAnswer1": " N2.","falseAnwser2": " CH4.","falseAnwer3": " NH3."}, {"id": "2   ","question":"Cho các phản ứng sau:(1) CuO + H2 → Cu + H2O(2) 2CuSO4+ 2H2O → 2Cu + O2+ 2H2SO4(3) Fe + CuSO4 → FeSO4+ Cu(4) 2Al + Cr2O3 → Al2O3 + 2CrSố phản ứng dùng để điều chế kim loại bằng phương pháp nhiệt luyện là.","trueAnswer": " 4","falseAnswer1": " 3","falseAnwser2": " 2","falseAnwer3": " 1"},{"id": "3","question":"Cặp chất nào sau đây không phản ứng được với nhau?","trueAnswer": " Metyl acrylat + H2(xt Ni, t0)","falseAnswer1": " Anilin + nước Br2","falseAnwser2": " Glucozơ + dung dịch AgNO3/NH3, đun nóng.","falseAnwer3": " Amilozơ + Cu(OH)2."},{"id": "4","question":"Tên của hợp chất CH3-CH2-NH-CH3 là?","trueAnswer": " N-metyletylamin","falseAnswer1": " Etylmetylamin.","falseAnwser2": " Metyletanamin","falseAnwer3": " Metyletylamin"},{"id": "5","question":"Để bảo vệ vỏ tàu biển, người ta thường dùng phương pháp nào sau đây?","trueAnswer": " Cách li kim loại với môi trường bên ngoài.","falseAnswer1": " Dùng chất ức chế sự ăn mòn.","falseAnwser2": " Dùng hợp kim chống gỉ.","falseAnwer3": " Dùng phương pháp điện hó"},{"id": "6   ","question":"Dãy các ion xếp theo chiều giảm dần tính oxi hóa là.","trueAnswer": " Ag+, Cu2+, Fe3+, Fe2+.","falseAnswer1": " Ag+, Fe3+, Cu2+, Fe2+.","falseAnwser2": " Fe3+, Ag+, Cu2+, Fe2+.","falseAnwer3": " Fe3+, Cu2+, Ag+, Fe2+."},{"id": "7   ","question":"Hợp chất nào sau đây thuộc loại đipeptit?","trueAnswer": " H2N-CH2-CO-NH-CH2-CO-NH-CH2-COOH.","falseAnswer1": " H2N-CH2-CH2-CO-NH-CH2-COOH","falseAnwser2": " H[HN-CH2-CH2-CO]2OH.","falseAnwer3": " H2N-CH2-CO-NH-CH(CH3)-COOH."},{"id": "8","question":"Trong số các loại tơ sau: tơ nitron; tơ visco; tơ nilon-6,6; tơ lapsan. Có bao nhiêu loại tơ thuộc tơ nhân tạo (tơ bán tổng hợp)?","trueAnswer": " 1.","falseAnswer1": " 3.","falseAnwser2": " 2.","falseAnwer3": " 4"},{"id": "9","question":"Hòa tan hết m g